import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './Validation.css';

const Validation = () => {
  const { id } = useParams();
  const [demandeList, setDemandeList] = useState([]);
  const [errors, setErrors] = useState([]);
  const [successMessage, setSuccessMessage] = useState('');
  const nav = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/validation/${id}`)
      .then((res) => {
        setDemandeList(res.data);
      })
      .catch((err) => {
        const errorResponse = err.response?.data?.errors || [];
        setErrors(Object.keys(errorResponse).map(key => errorResponse[key].message));
      });
  }, [id]);

  const validateDemande = (demandeID) => {
    axios.put(`http://localhost:5000/validation/${demandeID}`)
      .then(() => {
        setSuccessMessage("Demande has been validated successfully!");
        setDemandeList(demandeList.filter(demande => demande._id !== demandeID));
      })
      .catch((err) => {
        const errorResponse = err.response?.data?.errors || [];
        setErrors(Object.keys(errorResponse).map(key => errorResponse[key].message));
      });
  };

  return (
    <div className='validation-container'>
      {errors.map((err, index) => <p className='validation-errors' key={index}>{err}</p>)}
      <h2 className='validation-header'>Validation</h2>
      <div className='validation-list'>
        {demandeList.map((demande) => (
          <div key={demande._id} className='validation-item'>
            <p>Demande de: {demande.Motif}</p>
            <button className='validation-button' onClick={() => validateDemande(demande._id)}>Validate</button>
          </div>
        ))}
      </div>
      {successMessage && <p className="validation-success-message">{successMessage}</p>}
    </div>
  );
};

export default Validation;
