import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate, useParams } from 'react-router-dom';
import './Validation.css';

const Validation = () => {
  const [data, setData] = useState([]);
  const { id } = useParams();
  const [post, setPost] = useState('');
  const [errors, setErrors] = useState([]);
  const [demandes,Setdemandes]=useState([]);
  const nav = useNavigate();
  useEffect(() => {
    const fetchData = async () => {
      try {
        const demandeRes = await axios.get('http://localhost:5000/Demande');
        const demandes = demandeRes.data;
        Setdemandes(demandes);
        const dataRes = await axios.get('http://localhost:5000/Data');
        const employees = dataRes.data;

        const filteredEmployees = employees.filter(employee => 
          demandes.some(demande => demande.ID === employee._id)
        );

        setData(filteredEmployees);

        const userRes = await axios.get(`http://localhost:5000/Data/${id}`);
        setPost(userRes.data.post);
      } catch (err) {
        console.log(err);
        setErrors([err.message || 'An error occurred']);
      }
    };

    fetchData();
  }, [id]);

  const handleButtonClick = async (index) => {
    if (post.toUpperCase() === "RH") {
      const newData = [...data];
      newData[index].valid = !newData[index].valid;
      setData(newData);

      const sendObj = {
        solde: demandes[index]-demandes.numero,
        DateD: demandes[index].DateD,
        DateFt: demandes[index].DateFt,
        numero: demandes[index].numero,
        Motif: demandes[index].Motif,
        ID:demandes[index].ID,
      };

      try {
        await axios.put(`http://localhost:5000/Data/${newData[index]._id}`, sendObj);
        console.log('Data updated:', newData);
      } catch (err) {
        console.log(err);
        const errorResponse = err.response?.data?.errors || {};
        const errorArr = Object.values(errorResponse).map(e => e.message);
        setErrors(errorArr);
      }
    }
  };

  const handleDemandeClick = (index) => {
    if (post.toUpperCase() === "RH"){
    const employeeId = data[index]._id;
    nav(`/SeeDemande/${employeeId}`);
    }
  };

  return (
    <div className="validation-container">
      {errors.length > 0 && (
        errors.map((err, index) => <p key={index} className="error-message">{err}</p>)
      )}

      <Link to="/" className="validation-link">Accueil</Link>

      <table className="validation-table">
        <thead>
          <tr>
            <th>Nom</th>
            <th>Post</th>
            <th>Solde de Congé</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.name}</td>
              <td>{item.post}</td>
              <td>{item.solde}</td>
              <td>
                <button 
                  className="validation-button" 
                  onClick={() => handleButtonClick(index)}
                >
                  {item.valid ? 'Validé' : 'Non Validé'}
                </button>
                <button 
                  className="validation-button" 
                  onClick={() => handleDemandeClick(index)}
                >
                  Voir Demande
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Validation;
