import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './Demande.css';

const Demande = () => {
  const { id } = useParams();
  const [myuser, setMyUser] = useState([]);
  const [userDemande,SetuserDemande]=useState([])
  const [filteredData, setFilteredData] = useState([]);
  const [name, setName] = useState("");
  const [post, setPost] = useState("");
  const [valid, setValid] = useState(false);
  const [solde, setSolde] = useState(30);
  const [Motif, setMotif] = useState("");
  const [DateD, setDateD] = useState(new Date());
  const [DateFt, setDateFt] = useState(new Date());
  const [value, setValue] = useState(0);
  const [Numero, setNumero] = useState(0);
  const [newID, setnewID] = useState(id);
  const [errors, setErrors] = useState([])
  const nav = useNavigate();
  useEffect(() => {
    axios.get(`http://localhost:5000/Data/${id}`)
      .then((res) => {
        console.log(res.data);
        setMyUser(res.data);
        setName(res.data.name);
        setPost(res.data.post);
      })
      .catch(err=>{
        const errorResponse = err.response.data.errors; 
        console.log(errorResponse)
        const errorArr = []; 
        for (const key of Object.keys(errorResponse)) { 
            errorArr.push(errorResponse[key].message)
        }
        setErrors(errorArr);
    })
  }, [id]);

  useEffect(() => {
      axios.get(`http://localhost:5000/Demande`)
    .then((res) => {
      console.log(res.data)
        SetuserDemande(res.data);
        const filtered = res.data.filter((item) => {
            return (item.ID===newID) 
          });
          setFilteredData(filtered)
          if (filtered.length > 0) {
           
            setSolde(filtered[0].solde)
          }
      })
      .catch(err=>{
        const errorResponse = err.response.data.errors; 
        console.log(errorResponse)
        const errorArr = []; 
        for (const key of Object.keys(errorResponse)) { 
            errorArr.push(errorResponse[key].message)
        }
        setErrors(errorArr);
    }) 
  }, [id]);

  const handleIncrement = () => {
    if (value < solde) {
      setValue(value + 1);
      setSolde(solde - 1);
      setNumero(value+1);
    }
  };

  const handleDecrement = () => {
    if (value > 0) {
      setValue(value - 1);
      setSolde(solde + 1);
      setNumero(value-1);
    }
  };

  const add = () => {
    const sendObj = {
      solde,
      DateD,
      DateFt,
      Numero,
      Motif,
      ID:newID,
    };
    axios.post("http://localhost:5000/demande",sendObj)
      .then((res) => {
        console.log(res.data);
        console.log(valid);
        nav(`/validation/${myuser._id}`);
      })
      .catch(err=>{
        const errorResponse = err.response.data.errors; 
        console.log(errorResponse)
        const errorArr = []; 
        for (const key of Object.keys(errorResponse)) { 
            errorArr.push(errorResponse[key].message)
        }
        setErrors(errorArr);
    })
  };

  const valide = () => {
    if (post.toUpperCase() === 'RH') {
      nav(`/validation/${myuser._id}`);
    }
  };
  return (
    <div className='demand-container'>
      {errors.map((err, index) => <p className='errors' key={index}>{err}</p>)}
      <h2 className='demande-header'>Demande de Congé</h2>
      <div className='demande-details'>
        <p>Nom de salarié : {myuser.name}</p>
        <Link to="/">Accueil</Link>
        <p>Post : {myuser.post}</p>
        <p>Solde de congé : {solde}</p>
        <select id={id}>
          <option value="conge">Congé</option>
          <option value="autorisation">Autorisation</option>
        </select>
        <p>De :</p>
        <DatePicker
          className='date-picker'
          selected={DateD}
          onChange={(date) => setDateD(date)}
        />
        <p>A :</p>
        <DatePicker
          className='date-picker'
          selected={DateFt}
          onChange={(date) => setDateFt(date)}
        />
        <p>Numéro de Jour/Heure :</p>
        <button onClick={handleDecrement}>-</button>
        <input type="text" value={value} readOnly />
        <button onClick={handleIncrement}>+</button>
        <p>Motif :</p>
        <textarea onChange={(e) => setMotif(e.target.value)} cols="30" rows="5"></textarea>
        <button onClick={add}>Ajouter</button>
        <button onClick={valide}>Validation</button>
      </div>
    </div>
  );  
};
export default Demande;
