import React, { useState } from 'react'
import axios from 'axios'
import { Link, useNavigate } from 'react-router-dom'
import Inscription from './Inscription'
import eyeImage from '../Assets/eye.jpg';
const Accueil = () => {
 const [Login,SetLogin]=useState("")
 const  [MotDePasse,SetMotDePass]=useState("")
  const [errors, setErrors] = useState([]);
  const navigate=useNavigate()
  const [users,Setusers]=useState([])
  const [filteredData, setFilteredData] = useState({})
  const conexion =()=>{
    axios.get(`http://localhost:5000/Data`)
    .then((res) => {
        Setusers(res.data);
        const filtered = users.filter((item) => {
            return ( (item.name===Login) && (item.Passe===MotDePasse) ) 
          });
          setFilteredData(filtered)
          if (filtered.length > 0) {
            console.log(filtered[0]._id)
            navigate(`/Demande/${filtered[0]._id}`);
          }
      })
      .catch(err=>{
        const errorResponse = err.response.data.errors; 
        console.log(errorResponse)
        const errorArr = []; 
        for (const key of Object.keys(errorResponse)) { 
            errorArr.push(errorResponse[key].message)
        }
        setErrors(errorArr);
    })
  }
  return (
    <div style={{backgroundImage:`url(${eyeImage}`}}>
    <div className='accueil-container' style={{backgroundSize: 'cover', backgroundPosition: 'center' }}>
    <div className='acceuil'>
      {errors.length > 0 && errors.map((err, index) => <p key={index} style={{ color: 'red' }}>{err}</p>)}
      <h2>Plan TÃ¢che</h2>
      <h2>Saisissez votre login et mot de passe pour vous connecter</h2>
      <h3>Login</h3>
      <input 
        type="text" 
        onChange={(e) => SetLogin(e.target.value)} 
        style={{ marginBottom: '10px', padding: '8px', borderRadius: '4px', width: '100%' }}
      />
      <h3>Mot de Passe</h3>
      <input 
        type="password" 
        onChange={(e) => SetMotDePass(e.target.value)} 
        style={{ marginBottom: '10px', padding: '8px', borderRadius: '4px', width: '100%' }}
      /> 
      <br />
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginTop: '20px' }}>
        <button 
          onClick={conexion} 
          style={{ backgroundColor: 'red', color: 'white', padding: '10px 20px', margin: '10px', border: 'none', borderRadius: '4px' }}
        >
          Connexion
        </button>
        <p style={{ color: 'white', margin: '0 10px' }}>ou</p>
        <Link to='/Inscription'>
          <button 
            style={{ padding: '10px 20px', margin: '10px', border: 'none', borderRadius: '4px' }}
          >
            Inscription
          </button>
        </Link>
      </div>
    </div>
    </div>
    </div>
);
};

export default Accueil;
